// Conta caracteres da mensagem + 33,33% com limite de 500

// Obter o array de mensagens do input
const mensagens = $input.first().json.Mensagens;

// Concatenar todas as mensagens em uma string √∫nica
const mensagemCompleta = mensagens.join(' ');

// Contar caracteres da mensagem completa
const caracteresOriginais = mensagemCompleta.length;

// Calcular 33,33% a mais (multiplicar por 1.3333)
const caracteresComAcrescimo = Math.round(caracteresOriginais * 1.3333);

// Aplicar limite m√°ximo de 500
const caracteresFinais = Math.min(caracteresComAcrescimo, 500);

// Retornar o resultado
return [
  {
    json: {
      mensagem_original: mensagemCompleta,
      mensagens_array: mensagemCompleta, // Agora retorna a string concatenada
      caracteres_originais: caracteresOriginais,
      caracteres_com_acrescimo: caracteresComAcrescimo,
      caracteres_finais: caracteresFinais
    }
  }
