/**
 * C√≥digo para N8N - Node Code/Function
 * Aceita dados do INPUT e separa por par√°grafos
 * Sa√≠da: [{ content }]
 */

function chunkText(text) {
  // Separar por par√°grafos (quebras de linha duplas)
  const paragraphs = text.split(/\n\s*\n/).map(p => p.trim()).filter(Boolean);
  return paragraphs;
}

// Processar cada item do INPUT
const results = [];

for (const item of $input.all()) {
  const data = item.json;

  // Buscar dados em data, teste, ou diretamente no objeto
  let raw = data.data ?? data.teste ?? data;

  if (!raw) {
    throw new Error("Nenhum dado encontrado para processar.");
  }

  // Normalizar para array de objetos
  let records = [];

  if (typeof raw === 'string') {
    // Se √© uma string simples (n√£o JSONL), criar um objeto com ela
    try {
      // Tenta primeiro como JSONL: um JSON por linha
      const lines = raw.split(/\r?\n/).map(l => l.trim()).filter(Boolean);
      // Verifica se a primeira linha √© JSON v√°lido
      JSON.parse(lines[0]);
      records = lines.map(line => JSON.parse(line));
    } catch (e) {
      // Se n√£o √© JSONL, trata como texto simples
      records = [{ text: raw }];
    }
  } else if (Array.isArray(raw)) {
    records = raw;
  } else if (typeof raw === 'object') {
    records = [raw];
  } else {
    throw new Error("Formato de entrada inv√°lido.");
  }

  // Processar cada record
  for (const x of records) {
    const content = x.text ?? x.teste ?? `Pergunta: ${x.question}\nResposta: ${x.answer}`;

    // Dividir em par√°grafos
    const parts = chunkText(content);

    parts.forEach((part) => {
      results.push({
        json: {
          content: part
        }
      });
    });
  }
}

return results;

